import React from 'react';
import {useContext} from 'react';
import {RoomContext} from "../context";
import Title from '../components/Title';
//get all unique values
const getUnique = (items,value)=>{
    return [...new Set(items.map(item => item[value]))]
}

export default function RoomsFilter({rooms}) {
    const context = useContext(RoomContext);
    const{
        handleChange,type,capacity,price,minprice,
        maxprice,minsize,maxsize,breakfast,pets} 
        = context;
        //get unique types
        let types = getUnique(rooms,'type');
    //add all
    types = ['all',...types];
    //map to jsx
    types = types.map((item,index)=>{
        return <option value={item} key={index}>
            {item}
            </option>
    })
    let people = getUnique(rooms, 'capacity');
    people = people.map((item, index)=>{
        return <option key={index} value={item}>{item}</option>
    })
    return (
       <section className="filter-container">
           <Title title="search rooms"/>
           <form className="filter-form">
               {/* select type*/}
               <div className="from-group">
                   <label htmlFor="type">room type</label>
                   <select name="type" 
                   id="type" 
                   value={type}
                   className="form-control" 
                   onChange={handleChange}>
                     {types}
                   </select>
               </div>
               {/* end select type */}
               {/* guests */}
               <div className="from-group">
                   <label htmlFor="capacity">Guests</label>
                   <select name="capacity" 
                   id="capacity" 
                   value={capacity}
                   className="form-control" 
                   onChange={handleChange}>
                     {people}
                   </select>
               </div>
               {/* end guests */}
               {/*room price */}
               <div className="form-group">
                   <label htmlFor="price">
                       room price ${price}
                   </label>
                   <input type="range" name="price" min={minprice} max={maxprice} 
                   id="price" value={price} 
                   onChange={handleChange} className="form-control"/>
               </div>
               {/* end of room price */}
               {/*size */}
               <div className="form-group">
                   <label htmlFor="size">room size</label>
                   <div className="size-inputs">
                       <input type="number" name="minsize" id="size"
                       value={minsize} onChange={handleChange}
                       className="size-input"></input>
                       <input type="number" name="maxsize" id="size"
                       value={maxsize} onChange={handleChange}
                       className="size-input"></input>
                   </div>
               </div>
               {/* end size */}
           </form>
       </section>
    )
}
